var backbone = require('backbone');
var framework = require('framework');

//校验者
var validate = new framework.ModuleValidate();
//默认get函数
var getter = backbone.Model.prototype.get;
//默认set函数
var setter = backbone.Model.prototype.set;

//实体类基类定义
var BaseModel = backbone.Model.extend({
    __ClassName: 'base',
    initialize: function () { 
    },
    validate: function (attrs) {
        return validate.ValidateModel(attrs, this.__ClassName);
    },
    get: function (attributes, options) {
        return getter.call(this, attributes, options);;
    },
    set: function (attributes, v, options) {
        options = options || { validate: true };//默认会校验实体类
        return setter.call(this, attributes, v, options);;
    }
});

module.exports = BaseModel;