var IOUtil = require('fs-extra');
var GenUtil = function () { }
module.exports = new GenUtil();

/*
 * 名称：读取指定路径的模板文件内容
 * 参数(file)：模板文件路径
 */
GenUtil.prototype.ReadTemplate = function (file) {
    return IOUtil.readFileSync(file, 'utf-8');
}
/*
 * 名称：保存生成的代码为指定文件
 * 参数(file)：生成文件保存路径
 * 参数(context)：代码生成内容
 */
GenUtil.prototype.SaveFile = function (file, content) {
    IOUtil.writeFileSync(file, content);
}